import { useEffect, useState } from "react";
import superagent from 'superagent';

function App() {

    const [bookName,setBookName] = useState([]);
    const [books,setBooks] = useState([]);

    const handleInput = (e) => {
      setBookName(e.target.value);
  }

  console.log('before use effect',books);


  const handleSubmit = (e) => {
    e.preventDefault();
    // console.log('from click',bookName);
    superagent.get(`https://www.googleapis.com/books/v1/volumes?q=${bookName}`)
    .then((res) => {
      const result = res.body.items
      console.log(result);
      const finalResult = result.map((item) => {
        return item.volumeInfo;
        // console.log('from line 22 inside submit',books)
      })
      setBooks(finalResult);
    })
  }

  const handleClick = (e) => {
    e.preventDefault();
    setBooks(books.sort((a, b) => a.title.localeCompare(b.title)));


    books.map((item,idx) => {
        console.log('inside return handle click')
        return (
        <>
        <div>
        <h2 key={idx}>Title : {item.title}</h2>
        {
        item.authors ? <h4>Author : {item.authors[0]}</h4> : ['Unknown']
        }
        {
          item.imageLinks ? <img src={item.imageLinks.thumbnail}/> : <img src='https://yt3.ggpht.com/ytc/AKedOLQ37Vg0WC8TINK49uU4oL_L_qF7iDVE5C_gEjYang=s900-c-k-c0x00ffffff-no-rj'></img>
        }
        </div>
        </>
        )
      })


  }

  // console.log('books from line 54 after sort clicked',books);

  const handleButton = (e) => {
    e.preventDefault();
    let sortedByAuthor = books.map((book) => {
     book.authors.sort((a,b) => {a.localeCompare(b)});
     setBooks(sortedByAuthor);
    })
  }

  useEffect(() => {

  },[books]);



  // console.log('books from line 66',books);

  return (
    <>
    <div >
      <label>Book name</label>
      <input onChange={handleInput}></input>
      <button onClick={handleSubmit}>Search</button>
      <button onClick={handleClick}>Sort by tiile</button>
      <button onClick={handleButton}>Sort by author</button>
    </div>
    {
      books.map((book,idx) => {
      // console.log('inside map',book);
        return(
        <>
        <div>
        <h2 key={idx}>Title : {book.title}</h2>
        {
        book.authors ? <h4>Author : {book.authors[0]}</h4> : ['Unknown']
        }
        {
          book.imageLinks ? <img src={book.imageLinks.thumbnail}/> : <img src='https://yt3.ggpht.com/ytc/AKedOLQ37Vg0WC8TINK49uU4oL_L_qF7iDVE5C_gEjYang=s900-c-k-c0x00ffffff-no-rj'></img>
        }
        </div>
        </>
        )
      })
    }
  </>
  );
}

export default App;
