import { useState } from "react";

function App() {

  const [bookName,setBooks] = useState([]);

  const handleInput = (e) => {
   setBooks(e.target.value);
  }

  const handleClick = (e) => {
    e.preventDefault();
    console.log(handleInput);
    fetch(`https://www.googleapis.com/books/v1/volumes?q=${handleInput}`)
    .then((res) => {
      console.log(res.json());
    })
  }

  return (
    <>
    <div >
      <label>Book name</label>
      <input onChange={handleInput}></input>
      <button onClick={handleClick}>Search</button>
      <button>Sort by tiile</button>
      <button>Sort by author</button>
    </div>
  </>
  );
}

export default App;
