import { useState } from "react";
import superagent from 'superagent';

function App() {

  const [bookName,setBookName] = useState([]);
  const [books,setBooks] = useState([]);

  const handleInput = (e) => {
    setBookName(e.target.value);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('from click',bookName);
    superagent.get(`https://www.googleapis.com/books/v1/volumes?q=${bookName}`)
    .then((res) => {
      const result = res.body.items;
      const finalResult = result.map((item) => {
        // console.log(item.volumeInfo);
        setBooks(item.volumeInfo);
        // console.log('from line 22 inside submit',books)
      })
    })
  }

  const handleClick = (e) => {
    e.preventDefault();
    books.title.sort((a,b) => {
      // console.log(a-b);
      return a-b;
    })
  }

  const handleButton = (e) => {
    e.preventDefault();
    if(books.authors.length>0){
      books.authors.sort((a,b) => {
        // console.log(a-b);
        return a-b;
      })
    } else return '';
  }
  console.log('books from line 44',books);

  return (
    <>
    <div >
      <label>Book name</label>
      <input onChange={handleInput}></input>
      <button onClick={handleSubmit}>Search</button>
      <button onClick={handleClick}>Sort by tiile</button>
      <button onClick={handleButton}>Sort by author</button>
    </div>
    {/* {
      books.map((book,idx) => {
        return(
        <>
        <p key={idx}>{book.title}</p>
        <img>{book.imageLinks.thumbnail}</img>
        </>
        )
      })
    } */}
  </>
  );
}

export default App;
