import { useEffect, useState } from "react";
import superagent from 'superagent';

function App() {

    const [bookName,setBookName] = useState([]);
    const [books,setBooks] = useState([]);

    const handleInput = (e) => {
      setBookName(e.target.value);
  }

  console.log('before use effect',books);


  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('from click',bookName);
    superagent.get(`https://www.googleapis.com/books/v1/volumes?q=${bookName}`)
    .then((res) => {
      const result = res.body.items
      console.log(result);
      const finalResult = result.map((item) => {
      return item.volumeInfo;
        // console.log('from line 22 inside submit',books)
      })
      setBooks(finalResult);
    })
  }

  const handleClick = (e) => {
    e.preventDefault();
    setBooks(books.sort((a, b) => a.title.localeCompare(b.title)));
